const User=require("../models/user-model")
const bcrypt =require("bcryptjs")

const home= async(req,res)=>{
    try {
        res.status(200).send('Welcome by controller')
    } catch (error) {
        console.log(error);
        
    }
}

const register= async(req,res)=>{
    try {
        console.log(req.body);
        const {username,email,phone,passward}=req.body;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
        const userExists= await User.findOne({email})

        if(userExists){
            return res.status(400).json({message: "email already exists"})
        }
        
        const usercreate= await User.create({username,email,phone,passward});
        res.status(201).json({message: "Registeration Successful!!!",token: await usercreate.generateToken(),userId: usercreate._id.toString()})
    } catch (error) {
        // res.status(500).json("Internal server error");
        
        next(error);

        
    }
}


//login logic

const login= async (req,res)=>{
    try {
        const {email,passward} =req.body;

        const userExists= await User.findOne({email});
        if(!userExists){
            return res.status(400).json({message:"Invalid Credentials"})
        }

        const isPasswardValid=await userExists.comparePassward(passward);
        if(isPasswardValid){

            res.status(201).json({message: "Login Successful!!!",token: await userExists.generateToken(),userId: userExists._id.toString()})
            
        }else{
            res.status(401).json({message:"Invalid username or passward"})
        }

    } catch (error) {
        res.status(500).json("Internal server error");
        
        
    }
}

// user logic to send user data
const user=async (req,res)=>{
    try {
        
        const userData=req.user;
        
        return res.status(200).json({userData})

    } catch (error) {
        console.log(`error from the user route ${error}`)
        
    }
}
module.exports= {home,register,login,user}


































































































































































































































































































































































































































































































































































































































































































































































































































